import React, { useCallback } from "react";
import Spinner from "./Spinner";

function AuthForm({ button, title, formId }) {
  return (
    <div className="auth">
      <form
        className={`form ${formId}`}
        name="auth__form"
        // onSubmit={onSubmit}
        noValidate
      >
        <h2 className="auth__title">{title}</h2>

        <input
          id="name-input"
          type="text"
          className="auth__input"
          autoComplete="off"
          placeholder="Email"
          name="name"
          minLength="2"
          maxLength="40"
          // value={inputNameValue}
          // onChange={(e) => handleNameChange(e)}
          required
        />
        <span id="name-input-error" className="popup__input-error"></span>
        <input
          id="about-input"
          type="text"
          className="auth__input"
          autoComplete="off"
          placeholder="Пароль"
          name="pass"
          // value={inputAboutValue}
          minLength="2"
          maxLength="200"
          required
          // onChange={handleAboutChange}
        />
        <span id="about-input-error" className="popup__input-error"></span>

        <button className="auth__save" type="submit">
          {button}
        </button>
        {/* {isLoadingPopup ? <Spinner /> : ""} */}
      </form>
    </div>
  );
}

export default AuthForm;
