import React from 'react';

function PopupWithForm ({formId, isOpen, title, children, setIsOpen, button, setConfirmOpen}) {

  React.useEffect(() => {
    if(isOpen) {
      const Popup = document.querySelector(`.${formId}`);
      Popup.classList.add("popup_opened");
    }
    return () => {
      const Popup = document.querySelector(`.${formId}`);
      Popup.classList.remove("popup_opened");
    };
  }, [isOpen]); 

  return (
    <div className={`popup ${formId}`}>
    <form className={`form popup__container`} name={formId} noValidate>
      <h2 className="popup__title">{title}</h2>
      {children}
      <button className="popup__save" type="submit" onClick={function (evt) { evt.preventDefault(); setConfirmOpen(); setIsOpen(false)}}>{button}</button>
      <button className="popup__close" type="button" onClick={function (evt) { evt.preventDefault(); setIsOpen(false)}}></button>
    </form>
  </div>
  )
}

export default PopupWithForm;
