import React from 'react';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup';
import Confirm from './Confirm';
import Api from '../utils/Api';

import Header from './Header'
import Footer from './Footer'
import Main from './Main'
import EditProfilePopup from './EditProfilePopup'
import EditAvatarPopup from './EditAvatarPopup'


import CurrentUserContext from '../contexts/CurrentUserContext'


function App() {
  const [isEditProfilePopupOpen, setEditProfilePopupState] = React.useState(false);
  const [isEditAvatarPopupOpen, setEditAvatarPopupState] = React.useState(false);
  const [isAddPlacePopupOpen, setAddPlacerPopupState] = React.useState(false);
  const [isImagePopupOpen, setImagePopupState] = React.useState(false);
  const [isConfirmOpen, setConfirmOpen] = React.useState(false);


  const [selectedCard, setSelectedCard] = React.useState([]);

  const [currentUser, setСurrentUser] = React.useState([]); 




  const [inputCardName, setInputCardName] = React.useState([]);
  const [inputCardLink, setInputCardLink] = React.useState([]);


  const [confirmAction, setConfirmAction] = React.useState({});
  const [confirmObj, setConfirmObj] = React.useState({});




  React.useEffect(() => {
    Api.getUserInfo().then((res) => {
      setСurrentUser(res);
    })
    .catch((err) => {
      console.log(err); // выведем ошибку в консоль
    });
  }, []);

  function closeAllPopups() {
    setEditProfilePopupState(false);
    setEditAvatarPopupState(false);
    setAddPlacerPopupState(false);
    setImagePopupState(false);
    setSelectedCard([]);
  }

  function handleCardClick({src, nameImg}) {
    setSelectedCard({src, nameImg});
    setImagePopupState(true);
  }

  function handleConfirmClick() {
    setConfirmOpen(true);
  }

  function handleEditProfileClick (evt) {
    evt.preventDefault();
    setEditProfilePopupState(true);
  }

  function handleEditAvatarClick (e) {
    e.preventDefault();
    setEditAvatarPopupState(true);
  }

  function handleAddPlaceClick (evt) {
    evt.preventDefault();
    setAddPlacerPopupState(true);
  }

  function handleCardName (evt) {
    setInputCardName(evt.target.value);
   }

   function handleCardLink (evt) {
    setInputCardLink(evt.target.value);
   }

   function addNewCard (name, link) {
    setConfirmAction('addNewCard');
    setConfirmObj({
      name: name,
      link: link
    })
   }






  return (
    <CurrentUserContext.Provider value={currentUser}>
    <div className="App">
      <Header />
      <Main 
        onEditProfile = {(e) => handleEditProfileClick(e)} 
        onEditAvatar = {(e) => handleEditAvatarClick(e)}
        onAddPlace = {(e) => handleAddPlaceClick(e)}
        onCardClick = {handleCardClick}
        setConfirmOpen={handleConfirmClick}
        setConfirmAction={setConfirmAction}
        setConfirmObj={setConfirmObj}
        />
      <Footer />

      <EditProfilePopup isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} setConfirmOpen={handleConfirmClick} setConfirmAction={setConfirmAction} setConfirmObj={setConfirmObj}></EditProfilePopup>
      <EditAvatarPopup isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups} setConfirmOpen={handleConfirmClick} setConfirmAction={setConfirmAction} setConfirmObj={setConfirmObj}></EditAvatarPopup>




      <PopupWithForm 
        button="Создать" 
        title="Новое место" 
        formId="add-card" 
        isOpen={isAddPlacePopupOpen} 
        onClose={closeAllPopups} 
        setConfirmOpen={handleConfirmClick}
        confirmAction={() => addNewCard(inputCardName, inputCardLink)}
        setConfirmAction={setConfirmAction}

        children={<>
          <input
        id="name-input"
        type="text"
        className="popup__input popup__input_type_name"
        autoComplete="off"
        placeholder="Название"
        name="name"
        value={inputCardName}
        onChange={(e) => handleCardName(e)}
        minLength="2"
        maxLength="30"
        required
      />
      <span id="name-input-error" className="popup__input-error"></span>
      <input
        id="url-input"
        type="url"
        className="popup__input popup__input_type_about"
        autoComplete="off"
        placeholder="Ссылка на картинку"
        value={inputCardLink}
        onChange={(e) => handleCardLink(e)}
        name="link"
        required
      />
      <span id="url-input-error" className="popup__input-error"></span>
        </>}/>

      <Confirm isOpen={isConfirmOpen} setIsOpen={setConfirmOpen} confirmAction={confirmAction} confirmObj={confirmObj} setСurrentUser={setСurrentUser}/>
      <ImagePopup 
        card={selectedCard} 
        isOpen={isImagePopupOpen}   
        onClose={closeAllPopups}/>
    </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
