import React from "react";
import { Link, withRouter, useHistory } from "react-router-dom";
import AuthForm from "./AuthForm";
import * as Auth from "./Auth";

function Register({ setInfoTooltipOpen, setConfirmAuth }) {
  const [inputEmailValue, setInputEmailValue] = React.useState("");
  const [inputPassValue, setInputPassValue] = React.useState("");
  // const [userData, setUserData] = React.useState({
  //   id: "",
  //   message: "",
  // });

  const history = useHistory();

  function handleSubmit(e) {
    e.preventDefault();
    setInfoTooltipOpen(true);
    Auth.register(inputPassValue, inputEmailValue).then((data) => {
      console.log(data);
      // setUserData({ id: data.data._id });
      if (userData.id) {
        setConfirmAuth(true);
        setInfoTooltipOpen(true);
      } else {
        setConfirmAuth(false);
        setInfoTooltipOpen(true);
      }

      // history.push("/sign-in");
    });
  }

  function handleEmailChange(evt) {
    setInputEmailValue(evt.target.value);
  }

  function handlePassChange(evt) {
    setInputPassValue(evt.target.value);
  }

  return (
    <>
      <AuthForm
        button="Зарегистрироваться"
        title="Регистрация"
        formId="auth"
        onSubmit={(e) => handleSubmit(e)}
      >
        <input
          id="email-input"
          type="email"
          className="auth__input"
          autoComplete="off"
          placeholder="Email"
          name="email"
          value={inputEmailValue}
          onChange={handleEmailChange}
          required
        />
        <span id="email-input-error" className="auth__input-error"></span>
        <input
          id="pass-input"
          type="password"
          className="auth__input"
          autoComplete="new-password"
          placeholder="Пароль"
          name="pass"
          value={inputPassValue}
          minLength="2"
          maxLength="200"
          required
          onChange={handlePassChange}
        />
        <span id="pass-input-error" className="auth-error"></span>
      </AuthForm>
      <p className="auth__register-text">
        Уже зарегистрированы?{" "}
        <Link className="auth__register-link" to="/sign-in">
          Войти
        </Link>
      </p>
    </>
  );
}

export default withRouter(Register);
