import React from 'react';

function ImagePopup ({card, isOpen, closeImagePopup}) {

  const src = card.src;
  const nameImg = card.nameImg;

  React.useEffect(() => {
    console.log(isOpen);
    if(isOpen) {
      const Popup = document.querySelector('.image');
      Popup.classList.add("popup_opened");
    }
    return () => {
      const Popup = document.querySelector('.image');
      Popup.classList.remove("popup_opened");
    };
  }, [isOpen]); 

return (
  <div className="popup image">
    <div className="popup__container image__container">
      <img
        src={src}
        alt={nameImg}
        className="popup__image"
      />
      <p className="popup__figcaption-image">{nameImg}</p>
      <button className="popup__close" type="button" onClick={closeImagePopup}></button>

    </div>
  </div>
)

}

export default ImagePopup
