import React from 'react';
import PopupWithForm from './PopupWithForm';

import CurrentUserContext from '../contexts/CurrentUserContext'

function EditProfilePopup ({isOpen, onClose, setConfirmOpen, setConfirmAction, setConfirmObj}){

  const [inputAvatarValue, setInputAvatarValue] = React.useState([]);

  const currentUser = React.useContext(CurrentUserContext);


  function editUserAvatar (avatar) {
    setConfirmAction('editUserAvatar');
    setConfirmObj({
      avatar: avatar
    })
   }

   function handleAvatarChange (evt) {
    setInputAvatarValue(evt.target.value);
   }

  return (
    <PopupWithForm 
        button="Сохранить" 
        title="Обновить аватар" 
        formId="edit-user-avatar" 
        isOpen={isOpen} 
        onClose={onClose}
        setConfirmOpen={setConfirmOpen}
        confirmAction={() => editUserAvatar(inputAvatarValue)}
        setConfirmAction={setConfirmAction}

      children={<>
        <input
        id="avatar-input"
        type="url"
        className="popup__input popup__input_type_name"
        autoComplete="off"
        placeholder="Ссылка на аватар"
        name="name"
        onChange={handleAvatarChange}
        value={inputAvatarValue}
        minLength="2"
        maxLength="200"
        required
        />
        <span id="avatar-input-error" className="popup__input-error"></span>
      </>}/>
  )


}

export default EditProfilePopup
