import React from 'react';
import Api from '../utils/Api';
import Card from './Card';
import Spinner from './Spinner';
import CurrentUserContext from '../contexts/CurrentUserContext'




function Main ({onEditProfile, onAddPlace, onEditAvatar, onCardClick}) {
  // const [userName, setUserName] = React.useState([]); 
  // const [userDescription, setUserDescription] = React.useState([]); 
  // const [userAvatar, setUserAvatar] = React.useState([]); 
  const [cards, setCards] = React.useState([]); 
  const [isLoading, setIsLoading] = React.useState(false);

  const currentUser = React.useContext(CurrentUserContext);

  // React.useEffect(() => {
  //   Api.getUserInfo().then((res) => {
  //     setUserName(res.name);
  //     setUserDescription(res.about);
  //     setUserAvatar(res.avatar);
  //   })
  //   .catch((err) => {
  //     console.log(err); // выведем ошибку в консоль
  //   });
  // }, []);

  function handleCardLike({like}) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = like.some(i => i._id === currentUser._id);

    // Отправляем запрос в API и получаем обновлённые данные карточки
    // api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
    //     setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
    // });
    if(!isLiked) {
      Api.likeCard(currentUser._id);
    }
} 

function handleCardDelete (ownerId) {
  const isOwn = ownerId === currentUser._id;
  if(isOwn) {
    Api.deleteCard(currentUser._id);
  }
}

React.useEffect(() => {
  setIsLoading(true);
  Api.getInitialCards()
  .then((res) => {
    setCards(res.map((item) => ({
      src: item.link,
      nameImg: item.name,
      like: item.likes,
      cardId: item._id,
      ownerId: item.owner._id
    })));
    setIsLoading(false);
  })
  .catch((err) => {
    console.log(err); // выведем ошибку в консоль
  });
}, [])

 return ( 
 <main className="main page__main">
  <section className="profile main__profile">
    <a href="/#" className="profile__avatar-edit" onClick={onEditAvatar}>
      <img
        src={currentUser.avatar}
        alt="Аватар"
        className="profile__avatar"
      />
    </a>
    <div className="profile__info">
      <div className="profile__name">
        <p className="profile__name-title">{currentUser.name}</p>
        <button
          className="profile__button profile__button_type_edit"
          type="button"
          onClick={onEditProfile}
        ></button>
      </div>
      <p className="profile__about">{currentUser.about}</p>
    </div>
    <button
      className="profile__button profile__button_type_add "
      type="button"
      onClick={onAddPlace}
    ></button>
  </section>

        <section className="elements main__elements">

        {
            isLoading ? <Spinner /> : cards.map(({cardId, src, nameImg, like, ownerId}) => {return <Card key={cardId} ownerId={ownerId} src={src} nameImg={nameImg} like={like} cardId={cardId} onCardClick={onCardClick} onCardLike={handleCardLike} onCardDelete={handleCardDelete}/>}) 
        }                
        </section>

</main>)
}


export default Main;
