import React from 'react';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup';
import Confirm from './Confirm';

import Header from './Header'
import Footer from './Footer'
import Main from './Main'

function App() {
  const [isEditProfilePopupOpen, setEditProfilePopupState] = React.useState(false);
  const [isEditAvatarPopupOpen, setEditAvatarPopupState] = React.useState(false);
  const [isAddPlacePopupOpen, setAddPlacerPopupState] = React.useState(false);
  const [isImagePopupOpen, setImagePopupState] = React.useState(false);
  const [isConfirmOpen, setIsConfirmOpen] = React.useState(false);

  const [selectedCard, setSelectedCard] = React.useState([]);

  function handleCardClick({src, nameImg}) {
    setSelectedCard({src, nameImg});
    setImagePopupState(true);
  }

  function handleCardClose(){
    setImagePopupState(false);
    setSelectedCard([]);
  }

  function handleConfirmClick() {
    setIsConfirmOpen(true);
  }

  function handleEditProfileClick (evt) {
    evt.preventDefault();
    setEditProfilePopupState(true);
  }

  function handleEditAvatarClick (evt) {
    evt.preventDefault();
    setEditAvatarPopupState(true);
  }

  function handleAddPlaceClick (evt) {
    evt.preventDefault();
    setAddPlacerPopupState(true);
  }

  return (
    <div className="App">
      <Header />
      <Main 
        onEditProfile = {(e) => handleEditProfileClick(e)} 
        onEditAvatar = {(e) => handleEditAvatarClick(e)}
        onAddPlace = {(e) => handleAddPlaceClick(e)}
        onCardClick = {handleCardClick}
        />
      <Footer />
      <PopupWithForm button="Сохранить" title="Редактировать профиль" formId="edit-user" isOpen={isEditProfilePopupOpen} setIsOpen={setEditProfilePopupState} setConfirmOpen={handleConfirmClick}
      children={<>
        <input
          id="name-input"
          type="text"
          className="popup__input popup__input_type_name"
          autoComplete="off"
          placeholder="Имя"
          name="name"
          minLength="2"
          maxLength="40"
          required
        />
        <span id="name-input-error" className="popup__input-error"></span>
        <input
          id="about-input"
          type="text"
          className="popup__input popup__input_type_about"
          autoComplete="off"
          placeholder="О себе"
          name="about"
          minLength="2"
          maxLength="200"
          required
        />
        <span id="about-input-error" className="popup__input-error"></span>
        </>}
      />
      <PopupWithForm button="Сохранить" title="Обновить аватар" formId="edit-user-avatar" isOpen={isEditAvatarPopupOpen} setIsOpen={setEditAvatarPopupState} setConfirmOpen={handleConfirmClick}
      children={<>
        <input
        id="avatar-input"
        type="url"
        className="popup__input popup__input_type_name"
        autoComplete="off"
        placeholder="Ссылка на аватар"
        name="name"
        minLength="2"
        maxLength="200"
        required
        />
        <span id="avatar-input-error" className="popup__input-error"></span>
      </>}/>
      <PopupWithForm button="Создать" title="Новое место" formId="add-card" isOpen={isAddPlacePopupOpen} setIsOpen={setAddPlacerPopupState} setConfirmOpen={handleConfirmClick}
        children={<>
          <input
        id="name-input"
        type="text"
        className="popup__input popup__input_type_name"
        autoComplete="off"
        placeholder="Название"
        name="name"
        minLength="2"
        maxLength="30"
        required
      />
      <span id="name-input-error" className="popup__input-error"></span>
      <input
        id="url-input"
        type="url"
        className="popup__input popup__input_type_about"
        autoComplete="off"
        placeholder="Ссылка на картинку"
        name="link"
        required
      />
      <span id="url-input-error" className="popup__input-error"></span>
        </>}/>

      <Confirm isOpen={isConfirmOpen} setIsOpen={setIsConfirmOpen}/>
      <ImagePopup card={selectedCard} isOpen={isImagePopupOpen} closeImagePopup={handleCardClose}/>



    </div>
  );
}

export default App;
