import React from "react";
import AuthForm from "./AuthForm";
import { withRouter, useHistory } from "react-router-dom";

import * as Auth from "./Auth";

function Login({ handleLogin }) {
  const [inputEmailValue, setInputEmailValue] = React.useState("");
  const [inputPassValue, setInputPassValue] = React.useState("");

  const history = useHistory();

  function handleSubmit(e) {
    e.preventDefault();
    Auth.authorize(inputPassValue, inputEmailValue)
      .then((data) => {
        if (data.token) {
          handleLogin(e, inputEmailValue);
          setInputEmailValue(" ");
          setInputPassValue(" ");
          history.push("/");
        }
      })
      .catch((err) => {
        setInputEmailValue(" ");
        setInputPassValue(" ");
        console.log(err);
      });
  }

  function handleEmailChange(evt) {
    setInputEmailValue(evt.target.value);
  }

  function handlePassChange(evt) {
    setInputPassValue(evt.target.value);
  }

  return (
    <AuthForm
      button="Войти"
      title="Вход"
      formId="auth"
      onSubmit={(e) => handleSubmit(e)}
    >
      <input
        id="email-input"
        type="email"
        className="auth__input"
        autoComplete="off"
        placeholder="Email"
        name="email"
        value={inputEmailValue}
        onChange={handleEmailChange}
        required
      />
      <span id="email-input-error" className="auth__input-error"></span>
      <input
        id="pass-input"
        type="password"
        className="auth__input"
        autoComplete="off"
        placeholder="Пароль"
        name="pass"
        value={inputPassValue}
        minLength="2"
        maxLength="200"
        required
        onChange={handlePassChange}
      />
      <span id="pass-input-error" className="auth__input-error"></span>
    </AuthForm>
  );
}

export default withRouter(Login);
