  // }, [isOpen, formId, closeByOverlay, handleEscClose]);

  return (
    <div className={`infoTooltip ${isOpen && "popup_opened"}`}>
      <div className="infoTooltip__container">
        <img className="infoTooltip__img" alt={text}></img>
        <h2 className="infoTooltip__text">{text}</h2>
        <button
          className="infoTooltip__close"
          type="button"
          onClick={function (evt) {
            evt.preventDefault();
            onClose();
          }}
        ></button>
      </div>
    </div>
  );
}

export default InfoTooltip;
