import React from 'react';
import Api from '../utils/Api';
import Card1 from './Card';
import Spinner from './Spinner';



function Main ({onEditProfile, onAddPlace, onEditAvatar}) {
  const [userName, setUserName] = React.useState([]); 
  const [userDescription, setUserDescription] = React.useState([]); 
  const [userAvatar, setUserAvatar] = React.useState([]); 
  const [cards, setCards] = React.useState([]); 
  // const [isLoading, setIsLoading] = React.useState(false);
  let isLoading = false;
  let renderCards;


  React.useEffect(() => {
    Api.getUserInfo().then((res) => {
      setUserName(res.name);
      setUserDescription(res.about);
      setUserAvatar(res.avatar);
    })
    .catch((err) => {
      console.log(err); // выведем ошибку в консоль
    });
  }, [])

  React.useEffect(() => {
    // setIsLoading(true);
    isLoading = true;
    Api.getInitialCards()
        .then((res) => {
          setCards(res.map((item) => ({
            src: item.link,
            nameImg: item.name,
            like: item.likes.length,
            cardId: item._id, 
          })));
          console.log("===========");
          console.log(res);
          // console.log(cards);
        //  setIsLoading(false);
        isLoading = false;
    })
      .catch((err) => {
        console.log(err); // выведем ошибку в консоль
      });

      console.log(isLoading);
  }, []);



 return ( 
 <main className="main page__main">
  <section className="profile main__profile">
    <a href="" className="profile__avatar-edit" onClick={onEditAvatar}>
      <img
        src={userAvatar}
        alt="Аватар"
        className="profile__avatar"
      />
    </a>
    <div className="profile__info">
      <div className="profile__name">
        <p className="profile__name-title">{userName}</p>
        <button
          className="profile__button profile__button_type_edit"
          type="button"
          onClick={onEditProfile}
        ></button>
      </div>
      <p className="profile__about">{userDescription}</p>
    </div>
    <button
      className="profile__button profile__button_type_add "
      type="button"
      onClick={onAddPlace}
    ></button>
  </section>

        <section className="elements main__elements">
        {


            // isLoading ? <Spinner /> : cards.map((item) => <Card1 src={item.src} nameImg={item.nameImg} like={item.like} cardId={item.cardId} />) 
           cards.map(({cardId, ...props}) => <Card1 key={cardId} {...props}/>) 

        }

        </section>

</main>)
}


export default Main;
