import React from 'react';

function PopupWithForm ({formId, isOpen, title, children, onClose, button, setConfirmOpen}) {

  function closeByOverlay(evt){
    console.log(evt.target);
    console.log(evt.currentTarget);
    if (evt.target !== evt.currentTarget) return;
    onClose();
  }

  function handleEscClose (evt) {
    if (evt.key === "Escape") {
      onClose();
  }
}


  React.useEffect(() => {
    if(isOpen) {
      const Popup = document.querySelector(`.${formId}`);
      Popup.classList.add("popup_opened");
      document.body.addEventListener("keyup", handleEscClose);
      Popup.addEventListener("click", closeByOverlay);
    }
    return () => {
      const Popup = document.querySelector(`.${formId}`);
      Popup.classList.remove("popup_opened");
      document.body.removeEventListener("keyup", handleEscClose);
      Popup.removeEventListener("click", closeByOverlay);
    };
  }, [isOpen]); 

  return (
    <div className={`popup ${formId}`}>
    <form className={`form popup__container`} name={formId} noValidate>
      <h2 className="popup__title">{title}</h2>
      {children}
      <button className="popup__save" type="submit" onClick={function (evt) { evt.preventDefault(); setConfirmOpen(); onClose()}}>{button}</button>
      <button className="popup__close" type="button" onClick={function (evt) { evt.preventDefault(); onClose()}}></button>
    </form>
  </div>
  )
}

export default PopupWithForm;
