import React from 'react';
import PopupWithForm from './PopupWithForm';
import Header from './Header'
import Footer from './Footer'
import Main from './Main'

// import './App.css';

function App() {
  const [isEditProfilePopupOpen, setEditProfilePopupState] = React.useState(false);
  const [isEditAvatarPopupOpen, setEditAvatarPopupState] = React.useState(false);

//   isEditProfilePopupOpen
// isAddPlacePopupOpen
// isEditAvatarPopupOpen

  function handleEditProfileClick (e) {
    e.preventDefault();
    setEditProfilePopupState(true);
    // const editUserPopup = document.querySelector(".edit-user");
    // editUserPopup.classList.add("popup_opened");
  }

  function handleEditAvatarClick (e) {
    e.preventDefault();
    setEditAvatarPopupState(true);
  }


  function handleAddPlaceClick (e) {
    e.preventDefault();

  }




  return (
    <div className="App">
      <Header />
      <Main 
        onEditProfile = {() => handleEditProfileClick()} 
        onEditAvatar = {() => handleEditAvatarClick()}
        onAddPlace = {() => handleAddPlaceClick}
        />
      <Footer />
      <PopupWithForm title="Редактировать профиль" formId="edit-user" isOpen={isEditProfilePopupOpen} setIsOpen={setEditProfilePopupState}/>
      <PopupWithForm title="Обновить аватар" formId="edit-user-avatar" isOpen={isEditAvatarPopupOpen} setIsOpen={setEditAvatarPopupState}/>


    </div>
  );
}

export default App;
