import React from 'react';

function PopupWithForm (props) {
  if (props.isOpen) {
  // const Popup = document.querySelector(".edit-user");
  const Popup = document.querySelector(`.${props.formId}`);
    Popup.classList.add("popup_opened");
  }
  return (
    <div className={`popup ${props.formId}`}>
    <form className={`form popup__container`} name={props.formId} noValidate>
      <h2 className="popup__title">{props.title}</h2>
      {props.children}
      <button className="popup__save" type="submit">Сохранить</button>
      <button className="popup__close" type="button"></button>
    </form>
  </div>
  )
}

export default PopupWithForm;
