import React from 'react';
import CurrentUserContext from '../contexts/CurrentUserContext'

function Card ({cardId, src, nameImg, like, onCardClick}) {

  const currentUser = React.useContext(CurrentUserContext);

  function handleImgClick(){
    onCardClick({src, nameImg});
  }

  function handleTrashClick() {
    // Определяем, являемся ли мы владельцем текущей карточки
    const isOwn = cardId === currentUser._id;
    // Создаём переменную, которую после зададим в `className` для кнопки удаления
    const cardDeleteButtonClassName = (
      `element__trash ${isOwn ? '' : 'element__trash_invisible'}`
    ); 
    console.log(cardDeleteButtonClassName);
    console.log(isOwn);
    console.log("==============card !!!!!! ========");
  }

  return (
    <div className="element">
      <img src={src} alt={nameImg} className="element__img" onClick={handleImgClick}/>
      <div className="element__name">
        <h3 className="element__title">{nameImg}</h3>
        <div className="element__likes">
          <button className="element__like" type="button"></button>
          <p className="element__like-counter">{like}</p>
        </div>
      </div>
      <button className={cardDeleteButtonClassName} type="button"></button>
    </div>
  );
}

export default Card;
