import React from 'react';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup';
import Confirm from './Confirm';
import Api from '../utils/Api';

import Header from './Header'
import Footer from './Footer'
import Main from './Main'

import CurrentUserContext from '../contexts/CurrentUserContext'

function App() {
  const [isEditProfilePopupOpen, setEditProfilePopupState] = React.useState(false);
  const [isEditAvatarPopupOpen, setEditAvatarPopupState] = React.useState(false);
  const [isAddPlacePopupOpen, setAddPlacerPopupState] = React.useState(false);
  const [isImagePopupOpen, setImagePopupState] = React.useState(false);
  const [isConfirmOpen, setConfirmOpen] = React.useState(false);

  const [selectedCard, setSelectedCard] = React.useState([]);

  const [currentUser, setСurrentUser] = React.useState([]); 

  const [inputAboutValue, setInputAboutValue] = React.useState([]);
  const [inputNameValue, setInputNameValue] = React.useState([]);


  React.useEffect(() => {
    Api.getUserInfo().then((res) => {
      setСurrentUser(res);
    })
    .catch((err) => {
      console.log(err); // выведем ошибку в консоль
    });
  }, []);

  function closeAllPopups() {
    setEditProfilePopupState(false);
    setEditAvatarPopupState(false);
    setAddPlacerPopupState(false);
    setImagePopupState(false);
    setSelectedCard([]);
  }

  function handleCardClick({src, nameImg}) {
    setSelectedCard({src, nameImg});
    setImagePopupState(true);
  }

  function handleConfirmClick() {
    setConfirmOpen(true);
  }

  function handleEditProfileClick (evt) {
    evt.preventDefault();
    setInputAboutValue(currentUser.about);
    setInputNameValue(currentUser.name);
    setEditProfilePopupState(true);
  }

  function handleEditAvatarClick (e) {
    e.preventDefault();
    setEditAvatarPopupState(true);
  }

  function handleAddPlaceClick (evt) {
    evt.preventDefault();
    setAddPlacerPopupState(true);
  }

  function handleNameChange (evt) {
   setInputNameValue(evt.target.value);
  }

  function handleAboutChange (evt) {
   setInputAboutValue(evt.target.value);
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
    <div className="App">
      <Header />
      <Main 
        onEditProfile = {(e) => handleEditProfileClick(e)} 
        onEditAvatar = {(e) => handleEditAvatarClick(e)}
        onAddPlace = {(e) => handleAddPlaceClick(e)}
        onCardClick = {handleCardClick}
        />
      <Footer />
      <PopupWithForm 
        button="Сохранить" 
        title="Редактировать профиль" 
        formId="edit-user" 
        isOpen={isEditProfilePopupOpen} 
        onClose={closeAllPopups}
        setConfirmOpen={handleConfirmClick}
        children={<>
          <input
            id="name-input"
            type="text"
            className="popup__input popup__input_type_name"
            autoComplete="off"
            placeholder="Имя"
            name="name"
            minLength="2"
            maxLength="40"
            value={inputNameValue}
            onChange={(e) => handleNameChange(e)}
            required
          />
          <span id="name-input-error" className="popup__input-error"></span>
          <input
            id="about-input"
            type="text"
            className="popup__input popup__input_type_about"
            autoComplete="off"
            placeholder="О себе"
            name="about"
            value={inputAboutValue}
            minLength="2"
            maxLength="200"
            required
            onChange={handleAboutChange}
          />
          <span id="about-input-error" className="popup__input-error"></span>
          </>}
      />
      <PopupWithForm 
        button="Сохранить" 
        title="Обновить аватар" 
        formId="edit-user-avatar" 
        isOpen={isEditAvatarPopupOpen} 
        onClose={closeAllPopups}
        setConfirmOpen={handleConfirmClick}
      children={<>
        <input
        id="avatar-input"
        type="url"
        className="popup__input popup__input_type_name"
        autoComplete="off"
        placeholder="Ссылка на аватар"
        name="name"
        minLength="2"
        maxLength="200"
        required
        />
        <span id="avatar-input-error" className="popup__input-error"></span>
      </>}/>
      <PopupWithForm 
        button="Создать" 
        title="Новое место" 
        formId="add-card" 
        isOpen={isAddPlacePopupOpen} 
        onClose={closeAllPopups} 
        setConfirmOpen={handleConfirmClick}
        children={<>
          <input
        id="name-input"
        type="text"
        className="popup__input popup__input_type_name"
        autoComplete="off"
        placeholder="Название"
        name="name"
        minLength="2"
        maxLength="30"
        required
      />
      <span id="name-input-error" className="popup__input-error"></span>
      <input
        id="url-input"
        type="url"
        className="popup__input popup__input_type_about"
        autoComplete="off"
        placeholder="Ссылка на картинку"
        name="link"
        required
      />
      <span id="url-input-error" className="popup__input-error"></span>
        </>}/>

      <Confirm isOpen={isConfirmOpen} setIsOpen={setConfirmOpen}/>
      <ImagePopup 
        card={selectedCard} 
        isOpen={isImagePopupOpen}   
        onClose={closeAllPopups}/>
    </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
