import React from 'react';

function Card ({src, nameImg, like, onCardClick}) {

  function handleClick(){
    onCardClick({src, nameImg});
  }

  return (
    <div className="element">
      <img src={src} alt={nameImg} className="element__img" onClick={handleClick}/>
      <div className="element__name">
        <h3 className="element__title">{nameImg}</h3>
        <div className="element__likes">
          <button className="element__like" type="button"></button>
          <p className="element__like-counter">{like}</p>
        </div>
      </div>
      <button className="element__trash" type="button"></button>
    </div>
  );
}

export default Card;
