import React from "react";

function Login() {
  console.log("LOGIN!!!!!");

  const button = "Сохранить";
  const title = "Вход";
  const formId = "auth";

  return (
    <div className="auth">
      <form
        className={`form ${formId}`}
        name="auth__form"
        // onSubmit={onSubmit}
        noValidate
      >
        <h2 className="auth__title">{title}</h2>

        <input
          id="name-input"
          type="text"
          className="auth__input"
          autoComplete="off"
          placeholder="Имя"
          name="name"
          minLength="2"
          maxLength="40"
          // value={inputNameValue}
          // onChange={(e) => handleNameChange(e)}
          required
        />
        <span id="name-input-error" className="popup__input-error"></span>
        <input
          id="about-input"
          type="text"
          className="popup__input popup__input_type_about"
          autoComplete="off"
          placeholder="О себе"
          name="about"
          // value={inputAboutValue}
          minLength="2"
          maxLength="200"
          required
          // onChange={handleAboutChange}
        />
        <span id="about-input-error" className="popup__input-error"></span>

        <button className="auth__save" type="submit">
          {button}
        </button>
        {/* {isLoadingPopup ? <Spinner /> : ""} */}
      </form>
    </div>
  );
}

export default Login;
